mapper.update:
update notice 
set hit=IFNULL(hit,0) + 1 
WHERE notice_id = #{notice_id}

update notice

	title=#{title},
	content=#{content},
	state_code=#{state_code}

where notice_id=#{notice_id}

update notice
set state_code=9    
where notice_id=#{notice_id}

mapper.select:
select qna_id,q.account_id as account_id,name_kor,name_eng,name,title,content,group_no,divide_code,state_code,hit,register_date from qna as q left outer join employee as e on q.account_id = e.account_id left outer join member as m on q.account_id = m.account_id
title like CONCAT('%',#{searchKey},'%')
(title like CONCAT('%',#{searchKey},'%') or content like CONCAT('%',#{searchKey},'%'))
name like CONCAT('%',#{searchKey},'%') or name_kor like CONCAT('%',#{searchKey},'%')
and qna_id != ${qna_id}
and group_no = ${group_no}
and state_code!=9
order by group_no desc, qna_id asc limit ${startNum},${pageSize}

mapper.update:
update qna 
set hit=IFNULL(hit,0) + 1 
WHERE qna_id = #{qna_id}

update qna

	title=#{title},
	content=#{content},
	state_code=#{state_code}

where qna_id=#{qna_id}

update qna
set state_code=9    
where qna_id=#{qna_id}
