mapper.select:
select d.location , a.course_name , e.classroom , b.name_kor , a.course_start_date , h.lecture_register_name , a.course_end_date , c.capacity , f.lecture_register_code , g.name , a.open_course_id , f.account_id, (SELECT COUNT(1) FROM lecture_register h where h.OPEN_COURSE_ID = a.open_course_id ) as CNT from open_course a , employee b , subject c , education_center d , center_classroom e , lecture_register f , member g , code_lecture_register h where a.account_id = b.account_id and a.subject_id = c.subject_id and a.center_id = d.center_id and a.center_classroom_id = e.center_classroom_id and a.open_course_id = f.open_course_id and f.account_id = g.account_id
and d.center_id = #{center_id}
and f.lecture_register_code = h.lecture_register_code and f.lecture_register_code != '4' and STR_TO_DATE(#{today},'%Y-%m-%d') < a.course_start_date

select d.location , a.course_name , e.classroom , b.name_kor , a.course_start_date , h.lecture_register_name , a.course_end_date , c.capacity , f.lecture_register_code , g.name , a.open_course_id , f.account_id, (SELECT COUNT(1) FROM lecture_register h where h.OPEN_COURSE_ID = a.open_course_id ) as CNT from open_course a , employee b , subject c , education_center d , center_classroom e , lecture_register f , member g , code_lecture_register h where a.account_id = b.account_id and a.subject_id = c.subject_id and a.center_id = d.center_id and a.center_classroom_id = e.center_classroom_id and a.open_course_id = f.open_course_id and f.account_id = g.account_id
and d.center_id = #{center_id}
and f.lecture_register_code = h.lecture_register_code and f.lecture_register_code = '4' and STR_TO_DATE(#{today},'%Y-%m-%d') < a.course_start_date

mapper.update:
			update lecture_register set lecture_register_code = '2' 
                    where account_id = #{account_id} 
					and open_course_id = #{open_course_id}
					and lecture_register_code != '4'

		update lecture_register set lecture_register_code = '3' 
                    where account_id = #{account_id} 
					and open_course_id = #{open_course_id}
					and lecture_register_code != '4'

		update lecture_register set lecture_register_code = '4' 
                    where account_id = #{account_id} 
					and open_course_id = #{open_course_id}
